function darkMode(){let e=localStorage.getItem("mode");"dark"==e&&document.body.classList.add("dark-mode");document.querySelector(".dark-mode-button").addEventListener("click",(function(){document.body.classList.toggle("dark-mode"),e=document.body.classList.contains("dark-mode")?"dark":"light",localStorage.setItem("mode",e)}))}function hide(){const e=document.querySelector(".nav");window.innerWidth<768&&e.classList.add("hide")}function eventListeners(){document.querySelector(".mobile-menu img").addEventListener("click",(function(){responsiveNav()})),window.addEventListener("resize",(function(){responsiveNav_resize()}));document.querySelectorAll('input[name="contact-method"]').forEach(e=>e.addEventListener("click",showContactMethods))}function responsiveNav(){document.querySelector(".nav").classList.toggle("hide")}function responsiveNav_resize(){const e=window.innerWidth,n=document.querySelector(".nav");e<768?n.classList.add("hide"):n.classList.remove("hide")}function showContactMethods(e){const n=document.querySelector("#contact");"email"===e.target.value?n.innerHTML='\n            <label for="email">Email</label>\n            <input type="email" id="email" placeholder="Your email" name="email" required>\n        ':n.innerHTML='\n            <label for="phone">Phone number</label>\n            <input type="tel" id="phone" placeholder="Your phone" name="phone">\n\n            <p>Select the date and time.</p>\n\n            <label for="date">Date</label>\n            <input type="date" id="date" name="contact_date">\n\n            <label for="time">Time</label>\n            <input type="time" id="time" min="09:00" max="18:00" name="contact_time">\n        '}document.addEventListener("DOMContentLoaded",(function(){hide(),eventListeners(),darkMode()}));
